/**
 * @fileoverview Module for loading rules from files and directories.
 * @author Michael Ficarra
 */



//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const fs = require("fs"),
    path = require("path");

const rulesDirCache = {};

